void fun1(int* n, int ns, int* t, int* d) {
    int i = 0;
    while (i < ns) {
        d[i] = t[i] - n[i];
        i++;
    }
}

int fun2(int* d, int i, int csd) {
    return abs(d[i]) - abs(csd);
}

void fun3(int* d, int* csd, long long* to, int i) {
    int ao = fun2(d, i, *csd);
    *to += ao > 0 ? ao : 0;
    *csd = d[i];
}

void fun4(int* d, int* csd, long long* to, int i) {
    *to += abs(d[i]);
    *csd = d[i];
}

long long minimumOperations(int* n, int ns, int* t, int ts) {
    int* d = (int*)malloc(ns * sizeof(int));
    fun1(n, ns, t, d);
    long long to = 0;
    int csd = 0;
    int i = 0;
    while (i < ns) {
        ((i == 0) || ((d[i - 1] > 0) && (d[i] > 0)) || ((d[i - 1] < 0) && (d[i] < 0))) 
            ? fun3(d, &csd, &to, i) 
            : fun4(d, &csd, &to, i);
        i++;
    }
    free(d);
    return to;
}
