class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        vec = [nums[i] - target[i] for i in range(n)]

        ans = 0
        i = 0

        while i < n:
            if vec[i] == 0:
                i += 1
                continue

            j = i
            while j < n and vec[j] * vec[i] > 0:
                j += 1

            cur_max = abs(vec[i])
            ans += abs(vec[i])
            for k in range(i + 1, j):
                if cur_max < abs(vec[k]):
                    ans += abs(vec[k]) - cur_max

                cur_max = abs(vec[k])

            i = j

        return ans
