class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n);
        for(int i = 0; i<n; i++){
            diff[i] = (target[i]-nums[i]);
        }
        long long ans = 1ll*abs(diff[0]);
        for(int i = 1; i<n; i++) {
            long long ch = 1ll*diff[i]*diff[i-1];
            if(ch >= 0){
                if(abs(diff[i]) > abs(diff[i-1])){
                    ans += 1ll*(abs(diff[i])-abs(diff[i-1]));
                }
            } else {
                ans += 1ll*abs(diff[i]);
            }
        }
        return ans;
    }
};
