using LL = long long;

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();

        vector <LL> b(n + 1);
        for (int i = 0; i < n; i ++ )
        {
            b[i] = target[i] - nums[i];
        }

        vector <LL> c(n);
        c[0] = b[0];
        for (int i = 1; i < n; i ++ )
        {
            c[i] = b[i] - b[i - 1];
        }

        LL res = 0, ans = 0;
        for (int i = 0; i < n; i ++ )
        {
            if (c[i] >= 0) res += c[i];
            else ans -= c[i];
        }

        return max (res, ans);

    }

};
