class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [0] + [nums[i] - target[i] for i in range(len(nums))] + [0]
        diff_with_zeros = []
        for i in range(len(diff)):
            if diff[i] > 0 and diff[i-1] < 0 or diff[i] < 0 and diff[i-1] > 0:
                diff_with_zeros.append(0)
            diff_with_zeros.append(diff[i])
        # print(diff_with_zeros)
        operations = 0
        for i in range(1, len(diff_with_zeros)):
            operations += abs(diff_with_zeros[i] - diff_with_zeros[i-1])
        return operations // 2
