class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        long long ans=0;
        for(int i=0;i<n;){
         vector<int> temp;
         if(nums[i]>target[i]){
            while(i<n && nums[i]>target[i]){
                temp.push_back(nums[i]-target[i]);
                i++;
            }
         }
         else{
            while(i<n && nums[i]<=target[i]){
                temp.push_back(abs(nums[i]-target[i]));
                i++;
            }
         }
        //  for(auto &it:temp) cout<<it<<" ";
        //  cout<<endl;
         int tp=temp[0];
         for(int i=1;i<temp.size();i++){
            tp+=max(temp[i]-temp[i-1],(int)0);
         }
         ans+=tp;
        }
        return ans;
    }
};
