class Solution:
    def minimumOperations(self, nums: List[int], targets: List[int]) -> int:
        def is_same_sign(x: int, y: int):
            return x * y > 0
        diff = [targets[i] - nums[i] for i in range(len(nums))]
        prev = res = 0
        for d in diff:
            if is_same_sign(d, prev):
                if abs(d) < abs(prev):
                    res += abs(prev) - abs(d)
            else:
                res += abs(prev)
            prev = d
        return res + abs(prev)
