class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long a[100010], n = target.size(), sign, res = 0;
        for (int i = 0; i < n; i++) a[i] = target[i] - nums[i];
        sign = a[0] > 0 ? 1 : -1;
        res = abs(a[0]);
        for (int i = 1; i < n; i++) {
            if (a[i] )
            if (sign * a[i] > 0) {  // 同号
                if (abs(a[i]) > abs(a[i - 1]))
                    res += abs(a[i] - a[i - 1]);
            } else {
                sign = a[i] > 0 ? 1 : -1;
                res += abs(a[i]);
            }
        }
        return res;
    }
};
