class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        dif = [a-b for (a,b) in zip(nums, target)]
        cur = -1 if dif[0] < 0 else 1

        ans = 0
        curhigh = 0
        for i in range(len(dif)):
            h = dif[i]
            if cur > 0:
                if h >= 0:
                    if h > curhigh:
                        ans += h - curhigh
                        curhigh = h
                    else:
                        curhigh = h
                else:
                    ans += abs(h)
                    curhigh = h
                    cur = -1
            else:
                if h <= 0:
                    if h < curhigh:
                        ans += curhigh - h
                        curhigh = h
                    else:
                        curhigh = h
                else:
                    ans += abs(h)
                    curhigh = h
                    cur = 1
        return ans
