class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
       long long n= target.size();
       long long count =abs(target[0]-nums[0]);
       long long preopr = count;
       long long prechk = target[0]-nums[0]>0?1:target[0]-nums[0]<0?-1:0;
        for(long long i=1;i<n;i++){
            long long chk = target[i]-nums[i]>0?1:target[i]-nums[i]<0?-1:0;
            long long opr = abs(target[i]-nums[i]);
            if(prechk==chk){
                if(opr>preopr)count+=opr-preopr;
            }
            else{
                count+=opr;
            }
            prechk = chk;
            preopr = opr;
        }
        return count;
    }
};
