class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<long long>diff(n);
        for(int i=0;i<n;i++) diff[i]=target[i]-nums[i];
        long long ans=0;
        long long prv=0;
        long long cm=0;

        for(int i=0;i<n;i++){
            if(diff[i]>0){
                if(prv<=0){
                    ans+=cm;
                    cm=abs(diff[i]);
                }
                else{
                    if(diff[i]<cm){
                        ans+=cm-diff[i];
                        cm=diff[i];
                    }
                    else{
                        cm=diff[i];
                    }
                }
                prv=1;
            }
            else if(diff[i]<0){
                if(prv>=0){
                    ans+=cm;
                    cm=abs(diff[i]);
                }
                else{
                    if(abs(diff[i])<cm){
                        ans+=cm-abs(diff[i]);
                        cm=abs(diff[i]);
                    }
                    else{
                        cm=abs(diff[i]);
                    }
                }
                prv=-1;
            }
            else{
                if(prv!=0){
                    ans+=cm;
                }
                cm=0;
                prv=0;
            }

        }
        ans+=cm;
        return ans;
    }
};
