class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {

        long long n = target.size();
        // vector<long long>arr(n,0);
        for (long long i = 0; i < n; i++)
        {
            target[i] -= nums[i];
        }

        stack<long long>pq;
        long long ans = 0;
        if(target[0] < 0)
        {
            ans += abs(target[0]);
        }
        for (long long i = 0; i < n; i++)
        {
            // if(target[i] < 0)
            if(pq.empty())
            {
                pq.push(target[i]);

            }
            else if(pq.top() > target[i])
            {
                // cout<<"2 = "<<i<<endl;
                ans += pq.top() - target[i];
                long long temp = pq.top();
                while (!pq.empty() && temp == pq.top())
                {
                    pq.pop();
                }
                pq.push(target[i]);
            }
            else
            {
                // cout<<"3 = "<<i<<endl;
                pq.push(target[i]);

            }

        }

        // cout<<ans<<endl;
        if(pq.top()>0)
        {
            ans += pq.top();

        }
        return ans;

    }
};
