class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        arr = [target[i] - nums[i] for i in range(len(nums))]        
        ans = 0 
        temp = 0        
        for i in range(len(arr)): 
            if i == 0 or (arr[i - 1] > 0 and arr[i] > 0) or (arr[i - 1] < 0 and arr[i] < 0): 
                temp = abs(arr[i]) - abs(temp) 
                if temp > 0: 
                    ans += temp
                temp = arr[i] 
            else:   
                ans += abs(arr[i]) 
                temp = arr[i]         
        return ans
