class Solution {
public:
    long long minimumOperations(const vector<int>& source, const vector<int>& destination) {
        vector<int> differences(source.size());

        for (size_t i = 0; i < source.size(); ++i) {
            differences[i] = destination[i] - source[i];
        }

        long long total_steps = 0;
        long long segment_diff = 0;

        for (size_t i = 0; i < differences.size(); ++i) {
            if (i == 0 || (differences[i - 1] > 0 && differences[i] > 0) || (differences[i - 1] < 0 && differences[i] < 0)) {
                long long extra_steps = abs(differences[i]) - std::abs(segment_diff);
                if (extra_steps > 0) {
                    total_steps += extra_steps;
                }
                segment_diff = differences[i];
            } else {
                total_steps += abs(differences[i]);
                segment_diff = differences[i];
            }
        }

        return total_steps;
    }
};
