class Solution {
public:
    typedef long long ll;
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll ans=0;
        ll pre=0;
        for(ll i=0;i<nums.size();i++){
          ll val=target[i]-nums[i];
            if(val==0){
                pre=0;
            }
            else if(val<0){
                if(pre<0){
                    if(pre>val){
                         ans+=pre-val;
                    }
                }
                else{
                    ans+=abs(val);
                }
            }
            else{
                  if(pre>0){
                       if(pre<val){
                           ans+=val-pre;
                       }
                  }
                else{
                   ans+=val;
                }
            }
            pre=val;

        }
        return ans;
    }
};
