class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [j-i for i, j in zip(nums, target)]

        prev = 0
        ans = 0

        for i in diff:
            if i >= 0:
                if i > prev:
                    ans += (i-max(prev, 0))
            else:
                if i < prev:
                    ans += -(i-min(prev, 0))
            prev = i

        return ans
