class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        prec=0
        n=len(nums)
        newarr=[]
        ct=0
        for i in range(n):
            newarr.append(target[i]-nums[i])
        for i in range(n):
            if newarr[i] >=0:
                if prec<0:
                    prec=0
                if newarr[i]>=prec:
                    ct=ct+(newarr[i]-prec)
                    prec=newarr[i]
                elif newarr[i]<prec:
                    prec=newarr[i]
            else:
                if prec>=0:
                    prec=0
                if newarr[i]<=prec:
                    ct=ct+abs(newarr[i]-prec)
                    prec=newarr[i]
                elif newarr[i]>prec:
                    prec=newarr[i]
        return ct
