impl Solution {
    pub fn minimum_operations(nums: Vec<i32>, target: Vec<i32>) -> i64 {
        let mut diff = vec![0; nums.len()];
        for i in 0..nums.len() {
            diff[i] = nums[i] - target[i];
        }
        let mut result = i32::abs(diff[0]) as i64;
        let mut positve = if diff[0] < 0 {
            -1
        } else if diff[0] == 0 {
            0
        } else {
            1
        };
        let mut pre_max = i32::abs(diff[0]);
        // println!("{:?}", diff);
        for i in 1..diff.len() {
            //  println!("{}", result);
            let current_positive = if diff[i] < 0 {
                -1
            } else if diff[i] == 0 {
                0
            } else {
                1
            };
            if current_positive == positve && i32::abs(diff[i]) >= pre_max{
                    result += (i32::abs(diff[i]) - pre_max) as i64;
                    pre_max = i32::abs(diff[i]);
            }
            else if current_positive == positve && i32::abs(diff[i]) < pre_max{
                pre_max = i32::abs(diff[i]);
            }
            else{
                positve = current_positive; 
                pre_max = i32::abs(diff[i]);
                result += i32::abs(diff[i]) as i64;

            }

        }
        result
    }
}
