class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long sm=0;
        int n=nums.size();
        vector<int> v(n);
        for(int i=0;i<n;i++){
            v[i]=target[i]-nums[i];
        }
        int mxp=0,mxn=0;
        for(int i=0;i<n;i++){
            if(v[i]>0){
                if(i>0 and v[i-1]>v[i]){
                    sm+=(v[i-1]-v[i]);
                }
                mxp=v[i];
                sm+=mxn;
                mxn=0;
            }else if(v[i]<0){
                if(i>0 and v[i-1]<v[i]){
                    sm+=abs(v[i-1]-v[i]);
                }
                mxn=abs(v[i]);
                sm+=mxp;
                mxp=0;
            }else{
                sm+=mxp;
                sm+=mxn;
                mxp=0;
                mxn=0;
            }
        }
        sm+=mxn;
        sm+=mxp;
        return sm;
    }
};
