func minimumOperations(nums []int, target []int) int64 {
	flag := make([]int, len(nums))
	kk := make([]int, len(nums))
	for i := range nums {
		k := nums[i] - target[i]
		if k >= 0 {
			kk[i] = k
		}else {
			kk[i] = -k
			flag[i] = -1
		}
	}
	ans := 0

	for i := 0; i < len(kk); i++ {
		if i == 0 {
			ans += kk[0]
            continue
		}

		// 同号的情况
		if flag[i] == flag[i-1] {
			// 如果后面的大
			if kk[i] > kk[i-1] {
				ans = ans + kk[i]-kk[i-1]
			}
			// 前面的大就不管
		}else {
			//不同号无论如何都要移动的
			ans = ans + kk[i]
		}
	}
	return int64(ans)
}
