using ll = long long;

class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n = a.size();
        vector<int> diff(n);
        for (int i = 0; i < n; i++) {
            diff[i] = b[i] - a[i];
        }
        ll ans = abs(diff[0]);
        bool pos = (diff[0] >= 0 ? 1 : 0);
        for (int i = 1; i < n; i++) {
            if ((diff[i] >= 0) != pos) {
                pos = (diff[i] >= 0);
                ans += abs(diff[i]);
            } else {
                ans += (abs(diff[i]) - abs(diff[i - 1]) > 0 ? abs(diff[i]) - abs(diff[i - 1]) : 0);
            }
        }
        return ans;
    }
};
