    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long> arr1(nums.size(),0),arr2(nums.size(),0);
        long long n=nums.size(),minm=0;
        for(long long i=0;i<n;i++){
            long long a=target[i]-nums[i];
            if(a>0){
                arr1[i]=a;
            }else{
                arr2[i]=abs(a);
            }
        }
