class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> v;
        for(int i = 0;i<nums.size();i++){
            v.push_back(nums[i]-target[i]);
        }
        long long sum=0;
        long long last=0;
        for(auto it:v){
            if(it>last){
                sum+=it-last;
            }
            last=it;
        }
        if(0>last)sum+=-last;
        return sum;
    }
};
