class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size();

        for(int i = 0; i<n; i++){
            if(nums[i] > target[i]){
                long long int count = nums[i] - target[i];
                int j = i+1;
                while(j<n && nums[j] > target[j]){
                    long long int nc = nums[j] - target[j];
                    if(count >= nc){
                        ans += count-nc;
                        count = nc;
                    }
                    else{
                        count = nc;
                    }
                    j++;
                }
                i = j-1;
                ans += count;
            }
            else if(nums[i] < target[i]){
                long long int count = target[i] - nums[i];
                int j = i+1;
                while(j<n && nums[j] < target[j]){
                    long long int nc = target[j] - nums[j];
                    if(count >= nc){
                        ans += count-nc;
                        count = nc;
                    }
                    else{
                        count = nc;
                    }
                    j++;
                }
                i = j-1;
                ans += count;
            }
            else{
                continue;
            }
        }
        return ans;
    }
};
