class Solution 
{
    int isPos(int num)
    {
        return num == 0 ? 0 : num > 0 ? 1 : -1;
    }
public:
    long long minimumOperations(vector<int>& nums, vector<int>& t) 
    {
        long long res = 0;
        int n = nums.size();
        vector<int> diff(n);

        for(int i=0;i<n;i++)
        {
            diff[i] = t[i] - nums[i];
        }

        int i = 0;

        while(i<n)
        {
            int j = i + 1;
            res += abs(diff[i]);

            while(j<n && isPos(diff[i])!=0 && isPos(diff[j])!=0 && isPos(diff[j])==isPos(diff[i]))
            {
                res += max(abs(diff[j])-abs(diff[j-1]), 0);
                j++;
            }
            i = j;
        }

        return res;
    }
};
