class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        arr1 = [0] * n
        arr2 = [0] * n
        arr1[0] = nums[0]
        arr2[0] = target[0]
        for i in range(1, n):
            arr1[i] = nums[i] - nums[i - 1]
            arr2[i] = target[i] - target[i - 1]

        ans = add = sub = 0
        for i in range(n):
            if arr1[i] > arr2[i]:
                tmp = arr1[i] - arr2[i]
                if sub >= tmp:
                    sub -= tmp
                else:
                    ans += tmp - sub
                    add += tmp - sub
                    sub = 0
            elif arr1[i] < arr2[i]:
                tmp = arr2[i] - arr1[i]
                if add >= tmp:
                    add -= tmp
                else:
                    ans += tmp - add
                    sub += tmp - add
                    add = 0
        return ans
