#define ll long long
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll ans = 0;
        vector<ll> v;
        int n = nums.size();
        for(int i = 0 ; i < n ;i++)
        {
            v.push_back(1ll*(target[i] - nums[i]));
        }
        int cp = 1;
        if(v[0] < 0) cp--;
        ans += abs(v[0]);
        for(int i = 1; i < n ;i++)
        {
            if(v[i] == 0)
                continue;
            if(cp)
            {
                //+ve
                if(v[i] < 0)
                {
                    ans += abs(v[i]);
                    cp = 0;
                }
                else
                {
                    ans += max(v[i] - v[i-1],0ll);
                }
            }
            else
            {
                //-ve
                if(v[i] > 0)
                {
                    ans += v[i];
                    cp = 1;
                }
                else
                    ans += max(v[i-1] - v[i],0ll);
            }
            cout<<ans<<" ";
        }
        return ans;
    }
};
