class Solution {
public:
#define ll long long
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n,0);
        for(int i=0;i<n;i++)    diff[i] = target[i] - nums[i];
        ll dp[n][2];
        // dp[i][0] = inc , dp[i][1] = dec
        memset(dp,0,sizeof(dp));
        if(diff[0] > 0) dp[0][0] = diff[0];
        else dp[0][1] = abs(diff[0]);
        ll ans = abs(diff[0]);
        for(int i=1;i<n;i++){
            if(diff[i] > 0){
                if(diff[i-1] > 0){
                    if(diff[i] > diff[i-1]){
                        ans += (diff[i] - diff[i-1]);
                        dp[i][0] =  (diff[i]);
                    }
                    else{
                        dp[i][0] = diff[i];
                    }
                }
                else{
                    dp[i][0] = diff[i];
                    ans += diff[i];
                }
            }
            else if(diff[i] < 0){
                if(diff[i-1] < 0){
                    if(diff[i] < diff[i-1]){
                        ans += (diff[i-1] - diff[i]);
                        dp[i][1] = abs(diff[i]);
                    }
                    else{
                        dp[i][1] = abs(diff[i]);
                    }
                }
                else{
                    ans += abs(diff[i]);
                    dp[i][1] = abs(diff[i]);
                }
            }
        }
        return ans;
    }
};
