class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0,p=0,m=0;
        int tmp;
        for(int i=0;i<nums.size();i++){
            if(nums[i]<target[i]){
                if(p!=0){
                    tmp=target[i]-nums[i];
                    if(tmp>p)ans=ans+(tmp-p);
                    p=tmp;
                }
                else{
                    p=target[i]-nums[i];
                    ans=ans+p;
                }
                m=0;
            }
            else{
                if(m!=0){
                    tmp=nums[i]-target[i];
                    if(tmp>m)ans=ans+(tmp-m);
                    m=tmp;
                }
                else{
                    m=nums[i]-target[i];
                    ans=ans+m;
                }
                p=0;
            }
        }
        return ans;
    }
};
