class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long res = 0;
        int prev = 0;
        for(int i = 0; i < n; ++i){
            int diff = target[i] - nums[i];
            if(diff < 0){
                res += max(0, min(prev, 0) - diff);
            }
            else if(diff > 0){
                res += max(0, diff - max(prev, 0));
            }
            prev = diff;
        }
        return res;
    }
};
