class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int>arr;
        for(int i=0;i<nums.size();i++){
            arr.push_back(target[i]-nums[i]);
        }
        long long ans=0,calc2=0;
        bool check=true;
        int count=0;
        for(int i=0;i<arr.size();i++){
            if(arr[i]<0){
                if(check){
                    calc2=abs(arr[i]);
                    check=false;
                }
                if(count!=0){
                    calc2+=max(abs(arr[i])-abs(arr[i-1]),0);
                }
                count+=1;
            }
            else{
                count=0;
                check=true;
                ans+=calc2;
                calc2=0;
            }
        }
        ans+=calc2;
        check=true;
        calc2=0;
        count=0;
        for(int i=0;i<arr.size();i++){
            if(arr[i]>0){
                if(check){
                    calc2=arr[i];
                    check=false;
                }
                if(count!=0){
                    calc2+=max(arr[i]-arr[i-1],0);
                }
                count+=1;
            }
            else{
                count=0;
                check=true;
                ans+=calc2;
                calc2=0;
            }
        }
        ans+=calc2;
        return ans;
    }
};
