class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [(t - n) for n, t in zip(nums, target)]

        level = 0
        self.output = 0
        def reach(x, level):
            if x == 0:
                return
            if x < 0 and level > 0:
                x = -x
                level = 0
            if x > 0 and level < 0:
                level = 0
            if abs(x) > abs(level):
                self.output += abs(x) - abs(level)

        for x in diff:
            reach(x, level)
            level = x

        return self.output
