class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        def evalSS(ss):
            ss = [abs(val) for val in ss]
            prev = cnt = 0
            for x in ss:
                if x >= prev:
                    cnt += x - prev
                prev = x
            return cnt

        D = [x - y for x, y in zip(nums, target)]
        res = 0
        ss = []
        #print (D)
        for x in D:
            if x == 0:
                if ss:
                    res += evalSS(ss)
                ss = []
            elif not ss or ss[-1] * x > 0:
                ss.append(x)
            else:
                res += evalSS(ss)
                ss = [x]

        if ss:
            res += evalSS(ss)

        return res
