class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diffs = []
        for i in range(len(nums)):
            diffs.append(target[i]-nums[i])
        # print(diffs)
        arr = []
        flag = 0
        temp = []
        def getFlag(num):
            if num == 0:
                flag = 0
            elif num > 0:
                flag = 1
            else:
                flag = -1
            return flag
        for i in range(len(diffs)):
            currFlag = getFlag(diffs[i])
            # print(currFlag, flag, diffs[i], currFlag == flag or i == 0)
            if currFlag == flag or i == 0:
                if diffs[i] != 0:
                    temp.append(abs(diffs[i]))
            else:
                # print('a', temp.copy())
                if len(temp)>0:
                    arr.append(temp.copy())
                temp = []
                if diffs[i] != 0:
                    temp.append(abs(diffs[i]))
            flag = currFlag
        if len(temp)>0:
            arr.append(temp.copy())
        # print(arr)
        def getCount(sub):
            count = sub[0]
            for i in range(1, len(sub)):
                count += max(sub[i] - sub[i - 1], 0)
            return count
        res = 0
        for sub in arr:
            res += getCount(sub)
        return res
