class Solution {
    public long minimumOperations(int[] a, int[] t) {
        int n = a.length;
        for (int i = 0; i < n; i++) {
            a[i] -= t[i];
        }
        long ans = 0;
        ans += Math.abs(a[0]);
        for (int i = 1; i < n; i++) {
            if (a[i] > 0) {
                if (a[i - 1] > 0) {
                    if (a[i] - a[i - 1] > 0) ans += a[i] - a[i - 1];
                } else {
                    ans += a[i];
                }
            } else if (a[i] < 0) {
                if (a[i - 1] < 0) {
                    if (a[i] - a[i - 1] < 0) ans += Math.abs(a[i] - a[i - 1]);
                } else {
                    ans += Math.abs(a[i]);
                }
            }
        }
        return ans;
    }
}
