class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [target[i] - nums[i] for i in range(len(nums))]

        dp = abs(diff[0])
        for prev, cur in zip(diff, diff[1:]):
            if prev > 0 and cur > 0:
                dp += max(0, cur - prev)
            elif prev < 0 and cur < 0:
                dp += max(0, prev - cur)
            else:
                dp += abs(cur)

        return dp
