class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        def func(c, i, j):
            if c[i] < 0:
                for t in range(i, j+1):
                    c[t]*=-1
            ans = c[i]
            for t in range(i+1, j+1):
                if c[t] > c[t-1]:
                    ans += c[t]-c[t-1]
            return ans

        c = []
        for i in range(len(nums)):
            c.append(target[i]-nums[i])
        ans = 0            
        f = c[0]>=0
        i = 0
        for j in range(len(c)):
            if f:
                if c[j]<0:
                    ans += func(c, i, j-1)
                    f = False
                    i = j
            else:
                if c[j]>=0:
                    ans += func(c, i, j-1)
                    f = True
                    i = j
        return ans+func(c, i, j)
