    long long minimumOperations(vector<int>& a, vector<int>& b) {
        ll ans = 0;
        int n = a.size();
        std::vector<int> c(n);
        for (int i = 0; i < n; i++) {
            c[i] = b[i] - a[i];
        }

        std::vector<int> d(n + 1);
        int cur = 0;
        int add = 0, del = 0;
        for (int i = 0; i < n; i++) {
            if (cur == c[i]) {

            } else {
                if (c[i] > cur) {
                    int gap = std::min(c[i] - cur, del);
                    cur += gap;
                    del -= gap;
                    if (cur == c[i]) {

                    } else {
                        ans += c[i] - cur;
                        add += c[i] - cur;
                        cur = c[i];
                    }
                } else {
                    int gap = std::min(cur - c[i], add);
                    cur -= gap;
                    add -= gap;
                    if (cur == c[i]) {

                    } else {
                        ans += cur - c[i];
                        del += cur - c[i];
                        cur = c[i];
                    }
                }
            }

        }
        return ans;
    }
};
