class Solution {

    /**
     * @param Integer[] $nums
     * @param Integer[] $target
     * @return Integer
     */
    function minimumOperations($nums, $target) {
        $count = count($nums);
        $diff = [];
        foreach ($nums as $index => $num) {
            $diff[$index] = $target[$index] - $num;
        }
        $mem[$count - 1] = [abs($diff[$count - 1]), $diff[$count - 1]];

        for ($index = $count - 2; $index > -1; -- $index) {
            if ($diff[$index] == 0) {
                if ($mem[$index + 1][1] == 0) {
                    $mem[$index] = $mem[$index + 1];
                } else {
                    $mem[$index] = [$mem[$index + 1][0], 0];
                }
            } else if ($diff[$index] > 0) {
                if ($mem[$index + 1][1] > 0) {
                    if ($mem[$index + 1][1] < $diff[$index]) {
                        $l = $diff[$index];
                        $d = $diff[$index] - $mem[$index + 1][1];
                    } else {
                        $l = min($diff[$index], $mem[$index + 1][1]);
                        $d = 0;
                    }
                    $mem[$index] = [$mem[$index + 1][0] + $d, $l];
                } else {
                    $mem[$index] = [$mem[$index + 1][0] + $diff[$index], $diff[$index]];
                }
            } else {
                if ($mem[$index + 1][1] < 0) {
                    if ($mem[$index + 1][1] > $diff[$index]) {
                        $l = $diff[$index];
                        $d = $diff[$index] - $mem[$index + 1][1];
                    } else {
                        $l = max($diff[$index], $mem[$index + 1][1]);
                        $d = 0;
                    }
                    $mem[$index] = [$mem[$index + 1][0] - $d, $l];
                } else {
                    $mem[$index] = [$mem[$index + 1][0] - $diff[$index], $diff[$index]];
                }
            }
        }

        return $mem[0][0];
    }
}
