class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        d = list(y - x for y, x in zip(nums,  target))
        diff = [d[0]]
        for i in range(1 , len(d)):
            diff.append(d[i] - d[i - 1])
        diff.append(0)
        pos = sum(x for x in diff if x > 0 )
        neg = sum(-x for x in diff if x < 0)
        return max(pos , neg)
