class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& t) {
        ll n=a.size();
        ll ans=0;
        v<ll> d(n);
        for(ll i=0;i<n;i++)
        {
            d[i]=t[i]-a[i];
        }
        ll c=0;
        for(ll i=0;i<n;i++)
        {
            if(d[i]>=0)
            {
                if(d[i]>c)
                {
                    ans+=d[i]-c;
                    c=d[i];
                }
                else 
                {
                    c=d[i];
                }
            }
            else
            {
                c=0;
            }
        }
        c=0;
        for(ll i=0;i<n;i++)
        {
            if(d[i]<=0)
            {
                ll dc=abs(d[i]);
                if(dc>c)
                {
                    ans+=dc-c;
                    c=dc;
                }
                else
                {
                    c=dc;
                }
            }
            else 
            {
                c=0;
            }
        }

        return ans;
    }
};
