class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n);
        for (int i = 0; i < n; ++i) {
            diff[i] = target[i] - nums[i];
        }

        long long t = 0;
        int c = 0;

        for (int i = 0; i < n; ++i) {
            if (i == 0 || (diff[i - 1] > 0 && diff[i] > 0) || (diff[i - 1] < 0 && diff[i] < 0)) {
                long long a = abs(diff[i]) - abs(c);
                if (a > 0) {
                    t += a;
                }
                c = diff[i];
            } else {
                t += abs(diff[i]);
                c = diff[i];
            }
        }

        return t;
    }
};
