class Solution {

    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        for (int i=0; i<n; i++) {
            nums[i]-=target[i];
        }
        for (int i=n-1; i>0; i--) {
            nums[i]-=nums[i-1];
        }
        //System.out.println(Arrays.toString(nums));

        long m=0, p=0;
        for (int i=0; i<n; i++) {
            if (nums[i]<0) m-=nums[i];
            else p+=nums[i];
        }

        return Math.max(m,p);
    }
}
