#include <vector>
#include <cmath>
#include <algorithm>

class Solution {
public:
    long long max(long long a,long long b){
        if(a<=b)return b;
        return a;
    }
     long long min(long long a,long long b){
        if(a<=b)return a;
        return b;
    }
    long long minimumOperations(std::vector<int>& nums, std::vector<int>& target) {
        std::vector<long long> data(target.size());
        for (size_t i = 0; i < target.size(); ++i) {
            data[i] = target[i] - nums[i];
        }

        long long output = 0;
        for (size_t i = 0; i < data.size(); ++i) {
            size_t j = i;
            bool check = data[i] > 0;
            if (check) {
                while (i + 1 < data.size() && data[i + 1] > 0) {
                    ++i;
                }
            } else {
                while (i + 1 < data.size() && data[i + 1] < 0) {
                    ++i;
                }
            }

            output += std::abs(data[j]);
            if (check) {
                for (j = j + 1; j <= i; ++j) {
                    output += std::max(data[j] - data[j - 1], 0LL);
                }
            } else {
                for (j = j + 1; j <= i; ++j) {
                    output += std::abs(std::min(data[j] - data[j - 1], 0LL));
                }
            }
        }
        return output;
    }
};
