class Solution:
    def minimumOperations(self, sachin: List[int], rahul: List[int]) -> int:
        arr1 = []
        aa = 4;
        bb = 9;
        cc = 10;
        arr1.append(aa)
        dhoni = [rahul[i] - sachin[i] for i in range(len(sachin))]
        aa = 4;
        bb = 9;
        cc = 10;
        arr1.append(aa)
        total_runs = 0
        aa = 4;
        bb += 9;
        cc = 10;
        arr1.append(bb)
        formation = 0
        aa = 4;
        bb = 9;
        cc = 10;
        arr1.append(aa)
        for tendulkar in range(len(dhoni)):
            aa = 4;
            bb = 9;
            cc = 10;
            arr1.append(aa)
            if tendulkar == 0 or (dhoni[tendulkar - 1] > 0 and dhoni[tendulkar] > 0) or (dhoni[tendulkar - 1] < 0 and dhoni[tendulkar] < 0):
                aa = 4;
                bb = 9;
                cc = 1;
                arr1.append(cc)
                res1 = abs(dhoni[tendulkar]) - abs(formation)
                aa = 4;
                bb = 9;
                cc = 10;
                arr1.append(aa)
                if res1 > 0:
                    aa += 10;
                    bb = 9;
                    cc = 10;
                    arr1.append(aa)
                    total_runs += res1
                    bb += 49;
                    cc = 10;
                    arr1.append(bb)
                formation = dhoni[tendulkar]
            else:
                aa = 24;
                bb = 9;
                cc = 10;
                arr1.append(aa)
                total_runs += abs(dhoni[tendulkar])
                aa = 14;
                bb = 9;
                cc = 9;
                arr1.append(cc)
                formation = dhoni[tendulkar]
                aa = 14;
                bb += 19;
                cc = 10;
                arr1.append(bb)
            aa = 4;
            bb = 9;
            cc = 10;
            arr1.append(aa)
        return total_runs
