#define ll long long int
class Solution {
public:
    ll min_operations(vector<ll>& A)  
{  
    // Base Case  
    if (A.size() == 0)  
        return 0;  

    // Initialize ans to first element  
    ll ans = A[0];  

    for (ll i = 1; i < A.size(); i++) {  

        // For A[i] > A[i-1], operation  
        // (A[i] - A[i - 1]) is required  
        ans += max(A[i] - A[i - 1], (ll)0);  
    }  

    // Return the answer  
    return ans;  
} 
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll n=nums.size();
        vector<vector<ll>> store;
        vector<ll> abc;
        for(ll i=0;i<n;i++){
            ll see=target[i]-nums[i];
            if(see==0){
                if(abc.size()!=0){
                    store.push_back(abc);
                    abc.clear();
                }
            }else if(see>0){
                if(abc.size()!=0){
                    if(abc[0]>0){
                        abc.push_back(see);
                    }else{
                        store.push_back(abc);
                        abc.clear();
                        abc.push_back(see);
                    }
                }else{
                    abc.push_back(see);
                }
            }else if(see<0){
                if(abc.size()!=0){
                    if(abc[0]<0){
                        abc.push_back(see);
                    }else{
                        store.push_back(abc);
                        abc.clear();
                        abc.push_back(see);
                    }
                }else{
                    abc.push_back(see);
                }
            }
        }
        if(abc.size()!=0){
            store.push_back(abc);
        }
        ll ans=0;
        for(ll i=0;i<store.size();i++){
            vector<ll> see=store[i];
            for(ll j=0;j<see.size();j++){
                if(see[j]<0){
                    see[j]=-1*see[j];
                }
            }
            ll add= min_operations(see);
            ans=ans+add;
        }
        return ans;

    }
};
