class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        // diff arr = [1,1,1,2]
        int[] diff = new int[nums.length];
        for (int i = 0; i < nums.length; i++) {
            diff[i] = -nums[i] + target[i];
        }
        //System.out.println(Arrays.toString(diff));
        long ret = Math.abs(diff[0]);
        for (int i = 1; i < nums.length; i++) {
            if (diff[i] - diff[i-1] == 0) {
                //System.out.println("adding 0");
                continue;
            }
            if (Math.abs(diff[i]) < Math.abs(diff[i-1]) && ((diff[i] < 0 && diff[i-1] < 0) || (diff[i] > 0 && diff[i-1] > 0))) {
                //System.out.println("adding 0");
                continue;
            }
            //ret += Math.abs(diff[i] - diff[i-1]);
            //System.out.println("adding to ret: " + Math.min(Math.abs(diff[i] - diff[i-1]), Math.abs(diff[i])));
            ret += Math.min(Math.abs(diff[i] - diff[i-1]), Math.abs(diff[i]));
        }
        return ret;
    }
}
