class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> difs(n);

        for(int i=0;i<n;i++) {
            difs[i] = target[i] - nums[i];
        }

        long long ans = 0;
        stack<vector<int>> st;
        vector<int> segs;

        for(int i=0;i<n;i++) {
            if(segs.size()==0 && difs[i] != 0) {
                segs.push_back(difs[i]);
            } else if(difs[i]==0){
                if(segs.size()) {
                    st.push(segs);
                    segs.clear();
                }
            } else if ((long long)segs[0] * difs[i] > 0) {
                segs.push_back(difs[i]);
            } else {
                st.push(segs);
                segs.clear();
                segs.push_back(difs[i]);
            }
        }
        if(segs.size()) {
            st.push(segs);
            segs.clear();
        }

        while(!st.empty()) {
            vector<int> top = st.top();
            st.pop();
            int mind = INT_MAX;
            for(int i=0;i<top.size();i++) {
                mind = min(abs(top[i]), mind);
            }

            for(int &x : top) {
                x = abs(x) - mind;
            }
            ans += mind;

            for(int i=0;i<top.size();i++) {
                if(top[i]) {
                    segs.push_back(top[i]);
                } else if(segs.size()) {
                    st.push(segs);
                    segs.clear();
                }
            }
            if(segs.size()) {
                st.push(segs);
                segs.clear();
            }
        }
        return ans;
    }
};
