class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n=len(target)
        for i in range(n):
            target[i]-=nums[i]
        ans=abs(target[0])
        for i in range(1,n):
            if (target[i]>0 and target[i-1]>0):
                if target[i]>target[i-1]:ans+=target[i]-target[i-1]

            elif (target[i-1]<0 and target[i]<0):
                if target[i]<target[i-1]:ans+=target[i-1]-target[i]

            else:ans+=abs(target[i])
        return ans
