var minimumOperations = function(nums, target) {
    let diffArr = [], unique = {}, ans = 0;
    /*for(let i=0; i<nums.length; i++){
        let diff = for(let i=0; i<nums.length; i++){
        diffArr[i] = diff;
        if(unique[diff] === undefined){
            ans++;
            unique[diff] = true;
        }
    }*/
    let j=0,sign=1,curr=0,part=[];
    for(let i=0; i<nums.length; i++){
        let d = target[i] - nums[i];
        if(d>0){
            if(sign === 1){
                part.push(Math.abs(d));
            }else{
                ans += solve(part);
                sign = 1;
                part = [];
                part.push(Math.abs(d));
            }
        }else{
            if(sign === -1){
                part.push(Math.abs(d));
            }else{
                ans += solve(part);
                sign = -1;
                part = [];
                part.push(Math.abs(d));
            }
        }
    }
    ans += solve(part);
    function solve(arr){
        if(arr.length === 0){
            return 0;
        }
        let peak = arr[0];
        let count = peak;
        for(let i=0;i<arr.length;i++){
            let c = arr[i];
            if(c<=peak){
                peak = c;
            }else{
                let diff = c-peak;
                count += diff;
                peak = c;
            }
        }
        return count;
    }
    return ans;
};
